let boton = document.getElementById("btnRegistrar")
boton.addEventListener("click", evento=>{
    evento.preventDefault();
    registrarMantenimiento();
});

let registrarMantenimiento = async()=>{
    let campos = {};

    campos.id_empleado = document.getElementById("id_empleado").value;
    campos.id_carro = document.getElementById("id_carro").value;
    campos.id_taller = document.getElementById("id_taller").value;
    campos.calificacion = document.getElementById("calificacion").value;
    campos.fecha_inicio = document.getElementById("fecha_inicio").value;
    campos.fecha_salida_programada = document.getElementById("fecha_salida_programada").value;
    campos.costo = document.getElementById("costo").value;
    campos.detalle = document.getElementById("detalle").value;

    try {
        const response = await fetch("http://localhost:8080/api/registrar/mantenimiento", {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(campos)
        });
    
        if (!response.ok) {
            throw new Error('Error en la solicitud: ' + response.statusText);
        }
    
        const data = await response.json();
        mostrarExito('Registro exitoso: ' + data.message); // Mostrar mensaje de Ã©xito
    } catch (error) {
        mostrarError('Error al registrar mantenimiento: ' + error.message); // Mostrar mensaje de error
    }
};

function mostrarError(mensaje) {
    const errorElement = document.getElementById("error-message");
    errorElement.textContent = mensaje;
    errorElement.style.display = "block";
}
function mostrarExito(mensaje) {
    const successElement = document.getElementById("success-message");
    successElement.textContent = mensaje;
    successElement.style.display = "block";
}